import { useState, useEffect } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { useAppDispatch, useAppSelector } from "@/store/hooks";
import { fetchProducts } from "@/store/slices/productSlice";
import { placeOrder as placeOrderThunk } from "@/store/slices/orderSlice";
import { Product } from "../types";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { toast } from "sonner";
import { CreditCard, Banknote, ShoppingBag } from "lucide-react";

const QuickCheckout = () => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const dispatch = useAppDispatch();
  
  const { items: products = [], status: productsStatus } = useAppSelector((state) => state.products);
  const { status: orderStatus } = useAppSelector((state) => state.orders);
  
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [quantity, setQuantity] = useState(1);
  
  const loading = orderStatus === 'loading';
  const fetchingProducts = productsStatus === 'loading';

  const [formData, setFormData] = useState({
    user_name: "",
    user_phone: "",
    user_email: "",
    shipping_address: "",
    payment_method: "Cash on Delivery",
  });

  useEffect(() => {
    const loadProducts = async () => {
      try {
        await dispatch(fetchProducts(false)).unwrap(); // false for public products
      } catch (error) {
        console.error("Failed to fetch products", error);
        toast.error("Failed to load products");
      }
    };
    loadProducts();
  }, [dispatch]);

  useEffect(() => {
    if (productsStatus === 'succeeded' && products.length > 0) {
      const activeProducts = products.filter(
        (p) => p.isActive && p.stockQuantity > 0,
      );

      // Check for URL params
      const urlProductId = searchParams.get("productId");
      const urlQuantity = searchParams.get("quantity");
      const urlName = searchParams.get("name");
      const urlAddress = searchParams.get("address");
      const urlPhone = searchParams.get("phone");
      const urlEmail = searchParams.get("email");

      if (urlProductId) {
        const product = activeProducts.find(
          (p) => p.id === parseInt(urlProductId),
        );
        if (product) {
          setSelectedProduct(product);
          if (urlQuantity) {
            setQuantity(parseInt(urlQuantity));
          }
        }
      }

      if (urlName || urlAddress || urlPhone || urlEmail) {
        setFormData((prev) => ({
          ...prev,
          user_name: urlName || prev.user_name,
          shipping_address: urlAddress || prev.shipping_address,
          user_phone: urlPhone || prev.user_phone,
          user_email: urlEmail || prev.user_email,
        }));
      }
    }
  }, [productsStatus, products, searchParams]);

  const handleProductSelect = (productId: string) => {
    const activeProducts = products.filter(
      (p) => p.isActive && p.stockQuantity > 0,
    );
    const product = activeProducts.find((p) => p.id === parseInt(productId));
    setSelectedProduct(product || null);
    setQuantity(1);
  };

  const getTotalPrice = () => {
    if (!selectedProduct) return 0;
    return selectedProduct.price * quantity;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!selectedProduct) {
      toast.error("Please select a product");
      return;
    }

    if (
      !formData.user_name ||
      !formData.user_phone ||
      !formData.shipping_address
    ) {
      toast.error("Please fill in all required fields");
      return;
    }

    if (formData.user_phone.length < 10) {
      toast.error("Please enter a valid phone number");
      return;
    }

    if (quantity > selectedProduct.stockQuantity) {
      toast.error(`Only ${selectedProduct.stockQuantity} items available`);
      return;
    }

    try {
      const orderData = {
        ...formData,
        items: [
          {
            product_id: selectedProduct.id,
            quantity: quantity,
          },
        ],
      };

      const response = await dispatch(placeOrderThunk(orderData)).unwrap();
      toast.success("Order placed successfully!");
      navigate(`/order-confirmation/${response.order_id}`, {
        state: { orderDetails: response },
      });
    } catch (error: any) {
      console.error("Order placement failed:", error);
      const errorMessage = error.message || "Failed to place order";
      toast.error(errorMessage);
    }
  };

  if (fetchingProducts) {
    return (
      <div className="flex h-screen items-center justify-center">
        Loading products...
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="border-b">
        <div className="container mx-auto flex h-16 items-center px-4">
          <ShoppingBag className="mr-2 h-6 w-6" />
          <h1 className="text-2xl font-bold">Quick Checkout</h1>
        </div>
      </header>

      <div className="container mx-auto p-4 md:p-8">
        <div className="mx-auto max-w-4xl">
          <Card>
            <CardHeader>
              <CardTitle>Place Your Order</CardTitle>
              <CardDescription>
                Select a product and complete your order details
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6">
                {/* Product Selection */}
                <div className="space-y-4">
                  <Label htmlFor="product">Select Product *</Label>
                  <Select onValueChange={handleProductSelect}>
                    <SelectTrigger id="product">
                      <SelectValue placeholder="Choose a product" />
                    </SelectTrigger>
                    <SelectContent>
                      {products
                        .filter((p) => p.isActive && p.stockQuantity > 0)
                        .map((product) => (
                          <SelectItem
                            key={product.id}
                            value={product.id.toString()}
                          >
                            {product.name} - ${product.price} (
                            {product.stockQuantity} in stock)
                          </SelectItem>
                        ))}
                    </SelectContent>
                  </Select>

                  {selectedProduct && (
                    <div className="rounded-lg border p-4">
                      <div className="flex flex-col sm:flex-row gap-4">
                        {selectedProduct.imageUrl && (
                          <img
                            src={selectedProduct.imageUrl}
                            alt={selectedProduct.name}
                            className="h-48 w-full sm:h-24 sm:w-24 rounded object-cover"
                          />
                        )}
                        <div className="flex-1">
                          <h3 className="font-semibold">
                            {selectedProduct.name}
                          </h3>
                          <p className="text-sm text-muted-foreground">
                            {selectedProduct.description}
                          </p>
                          <div className="mt-2 flex items-center gap-2">
                            <span className="text-lg font-bold">
                              ${selectedProduct.price}
                            </span>
                            <Badge>
                              {selectedProduct.stockQuantity} available
                            </Badge>
                          </div>
                        </div>
                      </div>

                      <div className="mt-4 flex flex-col sm:flex-row sm:items-center gap-4">
                        <div className="flex items-center gap-4">
                          <Label htmlFor="quantity">Quantity:</Label>
                          <Input
                            id="quantity"
                            type="number"
                            min="1"
                            max={selectedProduct.stockQuantity}
                            value={quantity}
                            onChange={(e) =>
                              setQuantity(
                                Math.max(1, parseInt(e.target.value) || 1),
                              )
                            }
                            className="w-24"
                          />
                        </div>
                        <span className="text-sm text-muted-foreground">
                          Total:{" "}
                          <span className="font-bold">
                            ${getTotalPrice().toFixed(2)}
                          </span>
                        </span>
                      </div>
                    </div>
                  )}
                </div>

                <Separator />

                {/* Customer Information */}
                <div className="space-y-4">
                  <h3 className="font-semibold">Customer Information</h3>
                  <div className="grid gap-4 md:grid-cols-2">
                    <div className="space-y-2">
                      <Label htmlFor="name">Full Name *</Label>
                      <Input
                        id="name"
                        placeholder="John Doe"
                        value={formData.user_name}
                        onChange={(e) =>
                          setFormData({
                            ...formData,
                            user_name: e.target.value,
                          })
                        }
                        required
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="phone">Phone Number *</Label>
                      <Input
                        id="phone"
                        type="tel"
                        placeholder="1234567890"
                        value={formData.user_phone}
                        onChange={(e) =>
                          setFormData({
                            ...formData,
                            user_phone: e.target.value,
                          })
                        }
                        required
                      />
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="email">Email (Optional)</Label>
                    <Input
                      id="email"
                      type="email"
                      placeholder="john@example.com"
                      value={formData.user_email}
                      onChange={(e) =>
                        setFormData({ ...formData, user_email: e.target.value })
                      }
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="address">Shipping Address *</Label>
                    <Input
                      id="address"
                      placeholder="123 Main St, City, State, ZIP"
                      value={formData.shipping_address}
                      onChange={(e) =>
                        setFormData({
                          ...formData,
                          shipping_address: e.target.value,
                        })
                      }
                      required
                    />
                  </div>
                </div>

                <Separator />

                {/* Payment Method */}
                <div className="space-y-4">
                  <Label>Payment Method *</Label>
                  <RadioGroup
                    value={formData.payment_method}
                    onValueChange={(value) =>
                      setFormData({ ...formData, payment_method: value })
                    }
                  >
                    <div className="flex items-center space-x-3 rounded-lg border p-4 cursor-pointer hover:bg-accent">
                      <RadioGroupItem value="Cash on Delivery" id="cod" />
                      <Label
                        htmlFor="cod"
                        className="flex flex-1 cursor-pointer items-center gap-2"
                      >
                        <Banknote className="h-5 w-5" />
                        <div>
                          <p className="font-medium">Cash on Delivery</p>
                          <p className="text-sm text-muted-foreground">
                            Pay when you receive your order
                          </p>
                        </div>
                      </Label>
                    </div>
                    <div className="flex items-center space-x-3 rounded-lg border p-4 cursor-pointer hover:bg-accent">
                      <RadioGroupItem value="Debit Card" id="debit" />
                      <Label
                        htmlFor="debit"
                        className="flex flex-1 cursor-pointer items-center gap-2"
                      >
                        <CreditCard className="h-5 w-5" />
                        <div>
                          <p className="font-medium">Debit Card</p>
                          <p className="text-sm text-muted-foreground">
                            Pay securely with your debit card
                          </p>
                        </div>
                      </Label>
                    </div>
                  </RadioGroup>

                  {/* Dummy Stripe Form for Debit Card */}
                  {formData.payment_method === "Debit Card" && (
                    <div className="rounded-lg border p-4 space-y-4 bg-card animate-in fade-in slide-in-from-top-4">
                      <div className="flex items-center gap-2 mb-4">
                        <CreditCard className="h-5 w-5 text-primary" />
                        <h4 className="font-semibold">Card Details</h4>
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="card-number">Card Number</Label>
                        <Input
                          id="card-number"
                          placeholder="4242 4242 4242 4242"
                          maxLength={19}
                        />
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2">
                          <Label htmlFor="expiry">Expiration Date</Label>
                          <Input
                            id="expiry"
                            placeholder="MM/YY"
                            maxLength={5}
                          />
                        </div>
                        <div className="space-y-2">
                          <Label htmlFor="cvc">CVC</Label>
                          <Input
                            id="cvc"
                            placeholder="123"
                            maxLength={3}
                            type="password"
                          />
                        </div>
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="card-name">Cardholder Name</Label>
                        <Input id="card-name" placeholder="John Doe" />
                      </div>
                    </div>
                  )}
                </div>

                <Button
                  type="submit"
                  className="w-full"
                  size="lg"
                  disabled={loading || !selectedProduct}
                >
                  {loading
                    ? "Placing Order..."
                    : `Place Order - $${getTotalPrice().toFixed(2)}`}
                </Button>
              </form>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default QuickCheckout;

import { useState, useEffect } from 'react';
import { useAppDispatch, useAppSelector } from '@/store/hooks';
import { fetchBusinessDetails, createBusinessDetail, updateBusinessDetail, deleteBusinessDetail } from '@/store/slices/businessSlice';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
  DialogDescription,
} from '@/components/ui/dialog';
import { Loader2, Plus, Pencil, Trash2, FileText } from 'lucide-react';
import { toast } from 'sonner';
import { BusinessDetail } from '@/types/business';

const BusinessDetails = () => {
  const dispatch = useAppDispatch();
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [editingId, setEditingId] = useState<number | null>(null);
  const [formData, setFormData] = useState({ title: '', content: '' });

  const { details = [], status } = useAppSelector((state) => state.business);
  const isLoading = status === 'loading';

  useEffect(() => {
    dispatch(fetchBusinessDetails());
  }, [dispatch]);

  const handleCreate = async (data: typeof formData) => {
    try {
      await dispatch(createBusinessDetail(data)).unwrap();
      setIsAddOpen(false);
      setFormData({ title: '', content: '' });
      toast.success('Section created successfully');
    } catch (err) {
      toast.error('Failed to create section');
    }
  };

  const handleUpdate = async (id: number, data: typeof formData) => {
    try {
      await dispatch(updateBusinessDetail({ id, detail: data })).unwrap();
      setEditingId(null);
      setFormData({ title: '', content: '' });
      toast.success('Section updated successfully');
    } catch (err) {
      toast.error('Failed to update section');
    }
  };

  const handleDeleteAction = async (id: number) => {
    try {
      await dispatch(deleteBusinessDetail(id)).unwrap();
      toast.success('Section deleted successfully');
    } catch (err) {
      toast.error('Failed to delete section');
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingId) {
      handleUpdate(editingId, formData);
    } else {
      handleCreate(formData);
    }
  };

  const openEdit = (detail: BusinessDetail) => {
    setEditingId(detail.id);
    setFormData({ title: detail.title, content: detail.content });
    setIsAddOpen(true);
  };

  const handleDelete = (id: number) => {
    if (confirm('Are you sure you want to delete this section?')) {
      handleDeleteAction(id);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold tracking-tight text-slate-900">Business Details</h2>
          <p className="text-sm text-slate-500">Manage custom sections like About Us, Services, and Pricing.</p>
        </div>
        <Dialog open={isAddOpen} onOpenChange={(open) => {
          setIsAddOpen(open);
          if (!open) {
            setEditingId(null);
            setFormData({ title: '', content: '' });
          }
        }}>
          <DialogTrigger asChild>
            <Button className="gap-2 rounded-xl bg-slate-900 text-white hover:bg-slate-800">
              <Plus className="h-4 w-4" />
              Add Section
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-[500px]">
            <DialogHeader>
              <DialogTitle>{editingId ? 'Edit Section' : 'Add New Section'}</DialogTitle>
              <DialogDescription>
                Add a title and content for this business section. The AI agent will use this information.
              </DialogDescription>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4 py-2">
              <div className="space-y-2">
                <label className="text-sm font-medium leading-none">Title</label>
                <Input
                  placeholder="e.g. Our Services"
                  value={formData.title}
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                  required
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm font-medium leading-none">Content</label>
                <Textarea
                  placeholder="Describe your services here..."
                  className="min-h-[150px]"
                  value={formData.content}
                  onChange={(e) => setFormData({ ...formData, content: e.target.value })}
                  required
                />
              </div>
              <DialogFooter>
                <Button type="submit" disabled={false}>
                  {false && (
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  )}
                  {editingId ? 'Save Changes' : 'Create Section'}
                </Button>
              </DialogFooter>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {isLoading ? (
        <div className="flex h-[200px] items-center justify-center">
          <Loader2 className="h-8 w-8 animate-spin text-slate-400" />
        </div>
      ) : details?.length === 0 ? (
        <Card className="border-dashed">
          <CardContent className="flex flex-col items-center justify-center p-8 text-center">
            <div className="mb-4 rounded-full bg-slate-100 p-3">
              <FileText className="h-6 w-6 text-slate-400" />
            </div>
            <h3 className="mb-1 text-lg font-semibold text-slate-900">No details added yet</h3>
            <p className="mb-4 text-sm text-slate-500">
              Add sections regarding your business to help the AI understand your services better.
            </p>
            <Button variant="outline" onClick={() => setIsAddOpen(true)}>
              Add First Section
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {details?.map((detail) => (
            <Card key={detail.id} className="relative overflow-hidden transition-all hover:shadow-md">
              <CardHeader className="pb-3">
                <CardTitle className="text-lg font-semibold text-slate-900">{detail.title}</CardTitle>
                <CardDescription className="text-xs">
                  Updated {detail.updated_at ? new Date(detail.updated_at).toLocaleDateString() : 'recently'}
                </CardDescription>
              </CardHeader>
              <CardContent>
                <p className="line-clamp-4 whitespace-pre-wrap text-sm text-slate-600">
                  {detail.content}
                </p>
                <div className="mt-4 flex justify-end gap-2">
                  <Button
                    variant="ghost"
                    size="icon"
                    className="h-8 w-8 text-slate-500 hover:text-slate-900"
                    onClick={() => openEdit(detail)}
                  >
                    <Pencil className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="icon"
                    className="h-8 w-8 text-red-500 hover:bg-red-50 hover:text-red-600"
                    onClick={() => handleDelete(detail.id)}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};

export default BusinessDetails;

import { useState, useEffect } from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { MessageCircle, Search, Loader2, Send, Phone, Clock, ShoppingCart, Eye, User } from 'lucide-react';
import { useAppDispatch, useAppSelector } from '@/store/hooks';
import { fetchCustomerChats, fetchChatHistory, sendMessage } from '@/store/slices/chatSlice';
import { toast } from 'sonner';

interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: string;
}

interface Order {
  id: string;
  status: string;
  totalAmount: number;
  createdAt: string;
  itemCount?: number;
  paymentMethod?: string;
}

interface Conversation {
  phoneNumber: string;
  customerName: string;
  customerEmail: string;
  lastMessage: string;
  lastMessageTime: string;
  lastMessageRole: 'user' | 'assistant';
  messageCount: number;
  ongoingOrders: Order[];
  hasUnread: boolean;
}

interface ChatHistory {
  phoneNumber: string;
  customerName: string;
  customerEmail: string;
  customerAddress: string;
  joinedDate: string;
  messages: Message[];
  orders: Order[];
}

const CustomerChats = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedChat, setSelectedChat] = useState<string | null>(null);
  const [messageText, setMessageText] = useState('');
  const dispatch = useAppDispatch();

  const { conversations = [], status } = useAppSelector((state) => state.chats);
  const chatHistory = useAppSelector((state) => state.chats.activeChatHistory);
  const historyStatus = status;
  
  const isLoading = status === 'loading';
  const isLoadingHistory = historyStatus === 'loading';

  // Fetch conversations on mount and periodically
  useEffect(() => {
    dispatch(fetchCustomerChats());
    const interval = setInterval(() => {
      dispatch(fetchCustomerChats());
    }, 30000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, [dispatch]);

  // Fetch chat history when a chat is selected
  useEffect(() => {
    if (selectedChat) {
      dispatch(fetchChatHistory(selectedChat));
    }
  }, [selectedChat, dispatch]);

  const handleSendMessage = async () => {
    if (!messageText.trim() || !selectedChat) return;
    
    try {
      await dispatch(sendMessage({ phoneNumber: selectedChat, message: messageText.trim() })).unwrap();
      setMessageText('');
      toast.success('Message sent successfully!');
      // Refresh chat history and conversations
      dispatch(fetchChatHistory(selectedChat));
      dispatch(fetchCustomerChats());
    } catch (error: any) {
      toast.error(`Failed to send message: ${error.message}`);
    }
  };

  const filteredConversations = conversations.filter((conv: Conversation) =>
    conv.customerName.toLowerCase().includes(searchQuery.toLowerCase()) ||
    conv.phoneNumber.includes(searchQuery) ||
    conv.lastMessage.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const formatTime = (dateString: string) => {
    if (!dateString) return 'No messages';
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = (now.getTime() - date.getTime()) / (1000 * 60 * 60);
    
    if (diffInHours < 1) {
      return 'Just now';
    } else if (diffInHours < 24) {
      return `${Math.floor(diffInHours)}h ago`;
    } else {
      return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    }
  };

  const formatMessageTime = (dateString: string) => {
    return new Date(dateString).toLocaleString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getStatusColor = (status: string) => {
    const statusColors: Record<string, string> = {
      'PENDING': 'bg-yellow-100 text-yellow-700',
      'PAYMENT_REVIEW_REQUESTED': 'bg-blue-100 text-blue-700',
      'PAID': 'bg-green-100 text-green-700',
      'SHIPPED': 'bg-purple-100 text-purple-700',
      'COMPLETED': 'bg-green-100 text-green-700',
      'CANCELLED': 'bg-red-100 text-red-700',
    };
    return statusColors[status] || 'bg-gray-100 text-gray-700';
  };



  const totalConversations = conversations.length;
  const unreadCount = conversations.filter((conv: Conversation) => conv.hasUnread).length;
  const activeOrdersCount = conversations.reduce((acc: number, conv: Conversation) => 
    acc + conv.ongoingOrders.length, 0
  );

  if (isLoading) {
    return (
      <div className="flex h-96 items-center justify-center">
        <Loader2 className="h-6 w-6 animate-spin" />
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="space-y-2">
        <h1 className="text-2xl font-bold tracking-tight">Customer Chats</h1>
        <p className="text-muted-foreground">
          View and manage conversations with your customers
        </p>
      </div>

      {/* Stats Cards */}
      <div className="grid gap-4 md:grid-cols-3">
        <div className="rounded-lg border bg-card p-6 text-card-foreground shadow-sm">
          <div className="flex items-center justify-between space-y-0 pb-2">
            <h3 className="text-sm font-medium">Total Conversations</h3>
            <MessageCircle className="h-4 w-4 text-muted-foreground" />
          </div>
          <div className="text-2xl font-bold">{totalConversations}</div>
          <p className="text-xs text-muted-foreground">
            Active customer conversations
          </p>
        </div>
        <div className="rounded-lg border bg-card p-6 text-card-foreground shadow-sm">
          <div className="flex items-center justify-between space-y-0 pb-2">
            <h3 className="text-sm font-medium">Unread Messages</h3>
            <Badge variant="destructive" className="h-5 px-1.5 text-xs">
              {unreadCount}
            </Badge>
          </div>
          <div className="text-2xl font-bold">{unreadCount}</div>
          <p className="text-xs text-muted-foreground">
            Messages requiring response
          </p>
        </div>
        <div className="rounded-lg border bg-card p-6 text-card-foreground shadow-sm">
          <div className="flex items-center justify-between space-y-0 pb-2">
            <h3 className="text-sm font-medium">Active Orders</h3>
            <ShoppingCart className="h-4 w-4 text-muted-foreground" />
          </div>
          <div className="text-2xl font-bold">{activeOrdersCount}</div>
          <p className="text-xs text-muted-foreground">
            Customers with ongoing orders
          </p>
        </div>
      </div>

      {/* Search */}
      <div className="flex items-center space-x-2">
        <div className="relative flex-1">
          <Search className="absolute left-2.5 top-2.5 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search customers, phone numbers, or messages..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-8"
          />
        </div>
      </div>

      {/* Conversations Table */}
      <div className="rounded-md border">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>Customer</TableHead>
              <TableHead>Last Message</TableHead>
              <TableHead>Time</TableHead>
              <TableHead>Messages</TableHead>
              <TableHead>Ongoing Orders</TableHead>
              <TableHead>Actions</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredConversations.length === 0 ? (
              <TableRow>
                <TableCell colSpan={6} className="text-center py-8 text-muted-foreground">
                  {searchQuery ? 'No conversations match your search.' : 'No customer conversations yet.'}
                </TableCell>
              </TableRow>
            ) : (
              filteredConversations.map((conv: Conversation) => (
                <TableRow key={conv.phoneNumber}>
                  <TableCell>
                    <div className="flex items-center space-x-3">
                      <div className="flex h-8 w-8 items-center justify-center rounded-full bg-blue-100">
                        <User className="h-4 w-4 text-blue-600" />
                      </div>
                      <div>
                        <div className="font-medium">{conv.customerName}</div>
                        <div className="flex items-center text-sm text-muted-foreground">
                          <Phone className="h-3 w-3 mr-1" />
                          {conv.phoneNumber}
                        </div>
                      </div>
                      {conv.hasUnread && (
                        <Badge variant="destructive" className="h-4 w-4 rounded-full p-0">
                          <span className="sr-only">New message</span>
                        </Badge>
                      )}
                    </div>
                  </TableCell>
                  <TableCell className="max-w-xs">
                    <div className="truncate">
                      {conv.lastMessageRole === 'user' ? ' ' : ' '}
                      {conv.lastMessage}
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center text-sm text-muted-foreground">
                      <Clock className="h-3 w-3 mr-1" />
                      {formatTime(conv.lastMessageTime)}
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant="outline">{conv.messageCount}</Badge>
                  </TableCell>
                  <TableCell>
                    {conv.ongoingOrders.length > 0 ? (
                      <div className="flex flex-wrap gap-1">
                        {conv.ongoingOrders.slice(0, 2).map((order) => (
                          <Badge key={order.id} className={getStatusColor(order.status)}>
                            #{order.id}
                          </Badge>
                        ))}
                        {conv.ongoingOrders.length > 2 && (
                          <Badge variant="outline">+{conv.ongoingOrders.length - 2}</Badge>
                        )}
                      </div>
                    ) : (
                      <span className="text-muted-foreground">None</span>
                    )}
                  </TableCell>
                  <TableCell>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setSelectedChat(conv.phoneNumber)}
                    >
                      <Eye className="h-4 w-4 mr-1" />
                      View Chat
                    </Button>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </div>

      {/* Chat Dialog */}
      <Dialog open={!!selectedChat} onOpenChange={() => setSelectedChat(null)}>
        <DialogContent className="max-w-4xl max-h-[80vh]">
          <DialogHeader>
            <DialogTitle>
              Chat with {chatHistory?.customerName}
            </DialogTitle>
            <DialogDescription>
              Phone: {chatHistory?.phoneNumber} | Email: {chatHistory?.customerEmail}
            </DialogDescription>
          </DialogHeader>
          
          {isLoadingHistory ? (
            <div className="flex h-96 items-center justify-center">
              <Loader2 className="h-6 w-6 animate-spin" />
            </div>
          ) : chatHistory ? (
            <div className="space-y-4">
              {/* Customer Orders */}
              {chatHistory.orders.length > 0 && (
                <div className="rounded-lg border p-4">
                  <h4 className="font-medium mb-2">Customer Orders</h4>
                  <div className="flex flex-wrap gap-2">
                    {chatHistory.orders.slice(0, 5).map((order) => (
                      <Badge key={order.id} className={getStatusColor(order.status)}>
                        #{order.id} - ${order.totalAmount.toFixed(2)} ({order.status.toLowerCase()})
                      </Badge>
                    ))}
                  </div>
                </div>
              )}

              {/* Chat Messages */}
              <div className="max-h-96 overflow-y-auto space-y-3 border rounded-lg p-4">
                {chatHistory.messages.length === 0 ? (
                  <p className="text-center text-muted-foreground py-8">
                    No messages in this conversation yet.
                  </p>
                ) : (
                  chatHistory.messages.map((message) => (
                    <div
                      key={message.id}
                      className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                    >
                      <div
                        className={`max-w-xs lg:max-w-md px-3 py-2 rounded-lg ${
                          message.role === 'user'
                            ? 'bg-blue-500 text-white'
                            : 'bg-gray-100 text-gray-900'
                        }`}
                      >
                        <div className="text-sm">{message.content}</div>
                        <div
                          className={`text-xs mt-1 ${
                            message.role === 'user' ? 'text-blue-100' : 'text-gray-500'
                          }`}
                        >
                          {formatMessageTime(message.timestamp)}
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>

              {/* Send Message */}
              <div className="flex space-x-2">
                <Textarea
                  placeholder="Type your message..."
                  value={messageText}
                  onChange={(e) => setMessageText(e.target.value)}
                  className="flex-1"
                  rows={3}
                />
                <Button
                  onClick={handleSendMessage}
                  disabled={!messageText.trim()}
                  size="sm"
                >
                  {false ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    <>
                      <Send className="h-4 w-4 mr-1" />
                      Send
                    </>
                  )}
                </Button>
              </div>
            </div>
          ) : null}
          
          <DialogFooter>
            <Button variant="outline" onClick={() => setSelectedChat(null)}>
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default CustomerChats;

import { useMemo, useState, useEffect } from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Order, OrderStatus } from '@/types';
import { useToast } from '@/hooks/use-toast';
import { Eye, Filter, Loader2, Package, Search } from 'lucide-react';
import { useAppDispatch, useAppSelector } from '@/store/hooks';
import { fetchAdminOrders, updateOrderStatus } from '@/store/slices/orderSlice';

const Orders = () => {
  const { toast } = useToast();
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const dispatch = useAppDispatch();

  const { items: orders = [], status, error } = useAppSelector((state) => state.orders);
  const isLoading = status === 'loading';

  useEffect(() => {
    dispatch(fetchAdminOrders());
  }, [dispatch]);
  
  // Show error toast if fetch fails
  useEffect(() => {
    if (status === 'failed' && error) {
         toast({ title: 'Error', description: error, variant: 'destructive' });
    }
  }, [status, error, toast]);

  const handleStatusChange = async (orderId: number, newStatus: OrderStatus) => {
      try {
          await dispatch(updateOrderStatus({ orderId, status: newStatus })).unwrap();
          toast({ title: 'Status updated', description: 'Order status has been updated successfully' });
          if (selectedOrder?.id === orderId) {
            setSelectedOrder((prev) => (prev ? { ...prev, status: newStatus } : null));
          }
      } catch (err: any) {
          toast({ title: 'Error', description: err.message || 'Failed to update order status', variant: 'destructive' });
      }
  };

  const filteredOrders = useMemo(
    () =>
      orders.filter((order) => {
        const query = searchQuery.toLowerCase().trim();
        if (!query) return true;
        return (
          order.customerName.toLowerCase().includes(query) ||
          order.customerEmail.toLowerCase().includes(query) ||
          order.id.toString().includes(query)
        );
      }),
    [orders, searchQuery],
  );

  const orderGroups = useMemo(
    () => ({
      all: filteredOrders,
      pending: filteredOrders.filter((order) => order.status === 'pending'),
      paymentReview: filteredOrders.filter((order) => order.status === 'payment_review_requested'),
      paid: filteredOrders.filter((order) => order.status === 'paid'),
      shipped: filteredOrders.filter((order) => order.status === 'shipped'),
      completed: filteredOrders.filter((order) => order.status === 'completed'),
      cancelled: filteredOrders.filter((order) => order.status === 'cancelled'),
    }),
    [filteredOrders],
  );

  const totalRevenue = orders.reduce((sum, order) => sum + order.total, 0);
  const fulfillmentRate = orders.length
    ? Math.round(((orders.filter((o) => o.status === 'completed' || o.status === 'shipped').length / orders.length) * 100))
    : 0;

  const getStatusBadge = (status: OrderStatus) => {
    switch (status) {
      case 'pending':
        return <Badge className="rounded-md bg-amber-100 px-2.5 text-amber-700 hover:bg-amber-100">Pending</Badge>;
      case 'payment_review_requested':
        return <Badge className="rounded-md bg-orange-100 px-2.5 text-orange-700 hover:bg-orange-100">Payment Review</Badge>;
      case 'paid':
        return <Badge className="rounded-md bg-sky-100 px-2.5 text-sky-700 hover:bg-sky-100">Paid</Badge>;
      case 'shipped':
        return <Badge className="rounded-md bg-violet-100 px-2.5 text-violet-700 hover:bg-violet-100">Shipped</Badge>;
      case 'completed':
        return <Badge className="rounded-md bg-emerald-100 px-2.5 text-emerald-700 hover:bg-emerald-100">Completed</Badge>;
      case 'cancelled':
        return <Badge className="rounded-md bg-rose-100 px-2.5 text-rose-700 hover:bg-rose-100">Cancelled</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  const formatDate = (dateString: string) =>
    new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });

  const OrderTable = ({ orderList }: { orderList: Order[] }) => (
    <Table>
      <TableHeader>
        <TableRow className="border-slate-200 hover:bg-transparent">
          <TableHead className="text-xs font-semibold uppercase tracking-wide text-slate-500">Order ID</TableHead>
          <TableHead className="text-xs font-semibold uppercase tracking-wide text-slate-500">Customer</TableHead>
          <TableHead className="text-xs font-semibold uppercase tracking-wide text-slate-500">Items</TableHead>
          <TableHead className="text-xs font-semibold uppercase tracking-wide text-slate-500">Total</TableHead>
          <TableHead className="text-xs font-semibold uppercase tracking-wide text-slate-500">Date</TableHead>
          <TableHead className="text-xs font-semibold uppercase tracking-wide text-slate-500">Status</TableHead>
          <TableHead className="text-right text-xs font-semibold uppercase tracking-wide text-slate-500">Actions</TableHead>
        </TableRow>
      </TableHeader>
      <TableBody>
        {isLoading ? (
          <TableRow>
            <TableCell colSpan={7} className="py-12 text-center">
              <Loader2 className="mx-auto h-8 w-8 animate-spin text-slate-400" />
            </TableCell>
          </TableRow>
        ) : orderList.length === 0 ? (
          <TableRow>
            <TableCell colSpan={7} className="py-12 text-center text-slate-500">
              No matching orders found
            </TableCell>
          </TableRow>
        ) : (
          orderList.map((order) => (
            <TableRow key={order.id} className="border-slate-200 hover:bg-slate-50/70">
              <TableCell className="font-semibold text-slate-800">#{order.id}</TableCell>
              <TableCell>
                <div>
                  <p className="font-medium text-slate-800">{order.customerName}</p>
                  <p className="text-xs text-slate-500">{order.customerEmail}</p>
                </div>
              </TableCell>
              <TableCell className="text-slate-700">{order.items.length} items</TableCell>
              <TableCell className="font-semibold text-slate-800">${order.total.toFixed(2)}</TableCell>
              <TableCell className="text-slate-600">{formatDate(order.createdAt)}</TableCell>
              <TableCell>{getStatusBadge(order.status)}</TableCell>
              <TableCell className="text-right">
                <Button
                  variant="outline"
                  size="sm"
                  className="h-8 rounded-lg border-slate-200 bg-white text-slate-700 hover:bg-slate-100"
                  onClick={() => {
                    setSelectedOrder(order);
                    setIsDialogOpen(true);
                  }}
                >
                  <Eye className="mr-1 h-3.5 w-3.5" />
                  View
                </Button>
              </TableCell>
            </TableRow>
          ))
        )}
      </TableBody>
    </Table>
  );

  return (
    <div className="space-y-6">
      <div className="admin-surface flex flex-col gap-4 p-6 md:flex-row md:items-center md:justify-between">
        <div>
          <p className="text-sm font-medium uppercase tracking-[0.18em] text-slate-500">Order Management</p>
          <h1 className="mt-1 text-3xl font-bold tracking-tight text-slate-900">Orders</h1>
          <p className="mt-1 text-sm text-slate-500">Track, review, and update order fulfillment in one place.</p>
        </div>
      </div>

      <div className="grid gap-4 md:grid-cols-2 xl:grid-cols-4">
        <div className="admin-metric-card p-5">
          <p className="text-sm text-slate-500">Total Orders</p>
          <p className="mt-2 text-3xl font-bold text-slate-900">{orders.length}</p>
          <p className="mt-1 text-sm text-slate-500">Across all statuses</p>
        </div>
        <div className="admin-metric-card p-5">
          <p className="text-sm text-slate-500">Needs Review</p>
          <p className="mt-2 text-3xl font-bold text-slate-900">{orderGroups.pending.length + orderGroups.paymentReview.length}</p>
          <p className="mt-1 text-sm text-slate-500">Pending and payment checks</p>
        </div>
        <div className="admin-metric-card p-5">
          <p className="text-sm text-slate-500">Total Revenue</p>
          <p className="mt-2 text-3xl font-bold text-slate-900">${totalRevenue.toFixed(2)}</p>
          <p className="mt-1 text-sm text-slate-500">Value of all orders</p>
        </div>
        <div className="admin-metric-card p-5">
          <p className="text-sm text-slate-500">Fulfillment Rate</p>
          <p className="mt-2 text-3xl font-bold text-slate-900">{fulfillmentRate}%</p>
          <p className="mt-1 text-sm text-slate-500">Shipped + completed orders</p>
        </div>
      </div>

      <Tabs defaultValue="all" className="space-y-4">
        <div className="admin-surface p-4">
          <div className="flex flex-col gap-3 xl:flex-row xl:items-center xl:justify-between">
            <TabsList className="h-auto flex-wrap rounded-xl bg-slate-100 p-1.5">
              <TabsTrigger value="all" className="rounded-lg data-[state=active]:bg-white">All ({orderGroups.all.length})</TabsTrigger>
              <TabsTrigger value="pending" className="rounded-lg data-[state=active]:bg-white">Pending ({orderGroups.pending.length})</TabsTrigger>
              <TabsTrigger value="payment_review" className="rounded-lg data-[state=active]:bg-white">Review ({orderGroups.paymentReview.length})</TabsTrigger>
              <TabsTrigger value="paid" className="rounded-lg data-[state=active]:bg-white">Paid ({orderGroups.paid.length})</TabsTrigger>
              <TabsTrigger value="shipped" className="rounded-lg data-[state=active]:bg-white">Shipped ({orderGroups.shipped.length})</TabsTrigger>
              <TabsTrigger value="completed" className="rounded-lg data-[state=active]:bg-white">Completed ({orderGroups.completed.length})</TabsTrigger>
              <TabsTrigger value="cancelled" className="rounded-lg data-[state=active]:bg-white">Cancelled ({orderGroups.cancelled.length})</TabsTrigger>
            </TabsList>
            <div className="flex w-full items-center gap-2 xl:w-[360px]">
              <div className="relative flex-1">
                <Search className="pointer-events-none absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-slate-400" />
                <Input
                  placeholder="Search orders or customers"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="h-10 rounded-xl border-slate-200 bg-white pl-9"
                />
              </div>
              <Button variant="outline" size="icon" className="h-10 w-10 rounded-xl border-slate-200 bg-white">
                <Filter className="h-4 w-4 text-slate-600" />
              </Button>
            </div>
          </div>
        </div>

        <div className="admin-surface overflow-hidden p-2">
          <TabsContent value="all" className="m-0">
            <OrderTable orderList={orderGroups.all} />
          </TabsContent>
          <TabsContent value="pending" className="m-0">
            <OrderTable orderList={orderGroups.pending} />
          </TabsContent>
          <TabsContent value="payment_review" className="m-0">
            <OrderTable orderList={orderGroups.paymentReview} />
          </TabsContent>
          <TabsContent value="paid" className="m-0">
            <OrderTable orderList={orderGroups.paid} />
          </TabsContent>
          <TabsContent value="shipped" className="m-0">
            <OrderTable orderList={orderGroups.shipped} />
          </TabsContent>
          <TabsContent value="completed" className="m-0">
            <OrderTable orderList={orderGroups.completed} />
          </TabsContent>
          <TabsContent value="cancelled" className="m-0">
            <OrderTable orderList={orderGroups.cancelled} />
          </TabsContent>
        </div>
      </Tabs>

      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent className="max-w-3xl rounded-2xl border-slate-200 bg-white">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2 text-slate-900">
              <Package className="h-5 w-5 text-slate-600" />
              Order #{selectedOrder?.id}
            </DialogTitle>
            <DialogDescription>Order details and status management</DialogDescription>
          </DialogHeader>

          {selectedOrder && (
            <div className="space-y-5">
              <div className="grid gap-4 md:grid-cols-2">
                <div className="admin-subtle-surface p-4">
                  <h4 className="mb-3 font-semibold text-slate-900">Customer Information</h4>
                  <div className="space-y-2 text-sm text-slate-600">
                    <p><span className="font-medium text-slate-900">Name:</span> {selectedOrder.customerName}</p>
                    <p><span className="font-medium text-slate-900">Email:</span> {selectedOrder.customerEmail}</p>
                    <p><span className="font-medium text-slate-900">Address:</span> {selectedOrder.shippingAddress}</p>
                    <p><span className="font-medium text-slate-900">Payment:</span> {selectedOrder.paymentMethod}</p>
                  </div>
                </div>
                <div className="admin-subtle-surface p-4">
                  <h4 className="mb-3 font-semibold text-slate-900">Order Status</h4>
                  <Select
                    value={selectedOrder.status}
                    onValueChange={(value: OrderStatus) => handleStatusChange(selectedOrder.id, value)}
                  >
                    <SelectTrigger className="w-[220px] rounded-xl border-slate-200 bg-white">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="pending">Pending</SelectItem>
                      <SelectItem value="payment_review_requested">Payment Review</SelectItem>
                      <SelectItem value="paid">Paid</SelectItem>
                      <SelectItem value="shipped">Shipped</SelectItem>
                      <SelectItem value="completed">Completed</SelectItem>
                      <SelectItem value="cancelled">Cancelled</SelectItem>
                    </SelectContent>
                  </Select>
                  <p className="mt-3 text-sm text-slate-500">Ordered: {formatDate(selectedOrder.createdAt)}</p>
                </div>
              </div>

              <div className="admin-subtle-surface p-2">
                <Table>
                  <TableHeader>
                    <TableRow className="border-slate-200 hover:bg-transparent">
                      <TableHead>Product</TableHead>
                      <TableHead className="text-center">Quantity</TableHead>
                      <TableHead className="text-right">Price</TableHead>
                      <TableHead className="text-right">Subtotal</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {selectedOrder.items.map((item, index) => (
                      <TableRow key={index} className="border-slate-200 hover:bg-slate-50/60">
                        <TableCell>{item.productName}</TableCell>
                        <TableCell className="text-center">{item.quantity}</TableCell>
                        <TableCell className="text-right">${item.price.toFixed(2)}</TableCell>
                        <TableCell className="text-right">${(item.quantity * item.price).toFixed(2)}</TableCell>
                      </TableRow>
                    ))}
                    <TableRow className="border-slate-200 hover:bg-transparent">
                      <TableCell colSpan={3} className="text-right font-semibold text-slate-900">Total</TableCell>
                      <TableCell className="text-right text-lg font-bold text-slate-900">${selectedOrder.total.toFixed(2)}</TableCell>
                    </TableRow>
                  </TableBody>
                </Table>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default Orders;

import { useMemo, useState, useEffect } from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Product } from '@/types';
import { useToast } from '@/hooks/use-toast';
import { Plus, Pencil, Trash2, Search, Package, Loader2, Boxes } from 'lucide-react';
import { useAppDispatch, useAppSelector } from '@/store/hooks';
import { fetchProducts, createProduct, updateProduct, deleteProduct } from '@/store/slices/productSlice';
import { fetchCategories } from '@/store/slices/categorySlice';
import { uploadImage } from '@/services/adminService';

const Products = () => {
  const dispatch = useAppDispatch();
  const { toast } = useToast();

  const { items: products = [], status: productsStatus } = useAppSelector((state) => state.products);
  const { items: categories = [] } = useAppSelector((state) => state.categories);
  
  const isLoadingProducts = productsStatus === 'loading';

  useEffect(() => {
    dispatch(fetchProducts(true)); // true for admin
    dispatch(fetchCategories());
  }, [dispatch]);

  const [searchQuery, setSearchQuery] = useState('');
  const [categoryFilter, setCategoryFilter] = useState<string>('all');
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [productToDelete, setProductToDelete] = useState<Product | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    stockQuantity: '',
    categoryId: '',
    imageUrl: '',
    isActive: true,
  });

  const handleCreateProduct = async (productData: any) => {
    try {
      await dispatch(createProduct(productData)).unwrap();
      toast({ title: 'Product added', description: 'Product has been created successfully' });
      setIsDialogOpen(false);
      resetForm();
    } catch (err: any) {
      toast({ title: 'Error', description: err.message || 'Failed to create product', variant: 'destructive' });
    }
  };

  const handleUpdateProduct = async (id: number, data: Partial<Product>) => {
    try {
      await dispatch(updateProduct({ id, data })).unwrap();
      toast({ title: 'Product updated', description: 'Product has been updated successfully' });
      setIsDialogOpen(false);
      resetForm();
    } catch (err: any) {
      toast({ title: 'Error', description: err.message || 'Failed to update product', variant: 'destructive' });
    }
  };

  const handleDeleteProductAction = async (productId: number) => {
    try {
      await dispatch(deleteProduct(productId)).unwrap();
      toast({ title: 'Product deleted', description: 'Product has been deleted successfully' });
      setIsDeleteDialogOpen(false);
      setProductToDelete(null);
    } catch (err: any) {
      toast({ title: 'Error', description: err.message || 'Failed to delete product', variant: 'destructive' });
    }
  };

  const filteredProducts = useMemo(
    () =>
      products.filter((product) => {
        const matchesSearch =
          product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
          product.description.toLowerCase().includes(searchQuery.toLowerCase());
        const matchesCategory =
          categoryFilter === 'all' || categories.find((c) => c.id === product.categoryId)?.name === categoryFilter;
        return matchesSearch && matchesCategory;
      }),
    [products, searchQuery, categoryFilter, categories],
  );

  const lowStockCount = products.filter((product) => product.stockQuantity <= 10).length;
  const activeProducts = products.filter((product) => product.isActive).length;
  const inventoryValue = products.reduce((sum, product) => sum + product.price * product.stockQuantity, 0);

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      price: '',
      stockQuantity: '',
      categoryId: '',
      imageUrl: '',
      isActive: true,
    });
    setSelectedProduct(null);
  };

  const handleOpenAddDialog = () => {
    resetForm();
    setIsDialogOpen(true);
  };

  const handleOpenEditDialog = (product: Product) => {
    setSelectedProduct(product);
    setFormData({
      name: product.name,
      description: product.description,
      price: product.price.toString(),
      stockQuantity: product.stockQuantity.toString(),
      categoryId: product.categoryId.toString(),
      imageUrl: product.imageUrl || '',
      isActive: product.isActive,
    });
    setIsDialogOpen(true);
  };

  const handleFileUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    setIsUploading(true);
    try {
      const { imageUrl } = await uploadImage(file);
      setFormData((prev) => ({ ...prev, imageUrl }));
      toast({ title: 'Success', description: 'Image uploaded successfully' });
    } catch (error: unknown) {
      const errorMessage =
        typeof error === 'object' &&
        error !== null &&
        'response' in error &&
        typeof (error as { response?: unknown }).response === 'object' &&
        (error as { response?: { data?: { detail?: string } } }).response?.data?.detail
          ? (error as { response?: { data?: { detail?: string } } }).response?.data?.detail
          : 'Could not upload image';
      toast({
        title: 'Upload failed',
        description: errorMessage,
        variant: 'destructive',
      });
    } finally {
      setIsUploading(false);
    }
  };

  const handleSaveProduct = () => {
    if (!formData.name || !formData.price || !formData.categoryId) {
      toast({
        title: 'Missing fields',
        description: 'Please fill in all required fields',
        variant: 'destructive',
      });
      return;
    }

    if (selectedProduct) {
      handleUpdateProduct(selectedProduct.id, {
        name: formData.name,
        description: formData.description,
        price: parseFloat(formData.price),
        stockQuantity: parseInt(formData.stockQuantity) || 0,
        categoryId: parseInt(formData.categoryId),
        imageUrl: formData.imageUrl,
        isActive: formData.isActive,
      });
      return;
    }

    handleCreateProduct({
      name: formData.name,
      description: formData.description,
      price: parseFloat(formData.price),
      stockQuantity: parseInt(formData.stockQuantity) || 0,
      categoryId: parseInt(formData.categoryId),
      imageUrl: formData.imageUrl || 'https://images.unsplash.com/photo-1560393464-5c69a73c5770?w=300',
      isActive: true,
    });
  };

  const handleDeleteProduct = () => {
    if (productToDelete) {
      handleDeleteProductAction(productToDelete.id);
    }
  };

  return (
    <div className="space-y-6">
      <div className="admin-surface flex flex-col gap-4 p-6 md:flex-row md:items-center md:justify-between">
        <div>
          <p className="text-sm font-medium uppercase tracking-[0.18em] text-slate-500">Catalog Management</p>
          <h1 className="mt-1 text-3xl font-bold tracking-tight text-slate-900">Products</h1>
          <p className="mt-1 text-sm text-slate-500">Maintain product inventory, categories, and pricing.</p>
        </div>
        <Button onClick={handleOpenAddDialog} className="h-11 rounded-xl px-4 font-semibold">
          <Plus className="mr-2 h-4 w-4" />
          Add Product
        </Button>
      </div>

      <div className="grid gap-4 md:grid-cols-2 xl:grid-cols-4">
        <div className="admin-metric-card p-5">
          <p className="text-sm text-slate-500">Total Products</p>
          <p className="mt-2 text-3xl font-bold text-slate-900">{products.length}</p>
          <p className="mt-1 text-sm text-slate-500">{activeProducts} active listings</p>
        </div>
        <div className="admin-metric-card p-5">
          <p className="text-sm text-slate-500">Low Stock</p>
          <p className="mt-2 text-3xl font-bold text-slate-900">{lowStockCount}</p>
          <p className="mt-1 text-sm text-slate-500">Products with 10 or fewer units</p>
        </div>
        <div className="admin-metric-card p-5">
          <p className="text-sm text-slate-500">Categories</p>
          <p className="mt-2 text-3xl font-bold text-slate-900">{categories.length}</p>
          <p className="mt-1 text-sm text-slate-500">Distinct product categories</p>
        </div>
        <div className="admin-metric-card p-5">
          <p className="text-sm text-slate-500">Inventory Value</p>
          <p className="mt-2 text-3xl font-bold text-slate-900">${inventoryValue.toFixed(2)}</p>
          <p className="mt-1 text-sm text-slate-500">Estimated total stock value</p>
        </div>
      </div>

      <div className="admin-surface p-4">
        <div className="flex flex-col gap-3 md:flex-row">
          <div className="relative flex-1">
            <Search className="pointer-events-none absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-slate-400" />
            <Input
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="h-10 rounded-xl border-slate-200 bg-white pl-9"
            />
          </div>
          <Select value={categoryFilter} onValueChange={setCategoryFilter}>
            <SelectTrigger className="h-10 w-full rounded-xl border-slate-200 bg-white md:w-[240px]">
              <SelectValue placeholder="All Categories" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Categories</SelectItem>
              {categories.map((category) => (
                <SelectItem key={category.id} value={category.name}>
                  {category.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="admin-surface overflow-hidden p-2">
        <Table>
          <TableHeader>
            <TableRow className="border-slate-200 hover:bg-transparent">
              <TableHead className="text-xs font-semibold uppercase tracking-wide text-slate-500">Image</TableHead>
              <TableHead className="text-xs font-semibold uppercase tracking-wide text-slate-500">Product</TableHead>
              <TableHead className="text-xs font-semibold uppercase tracking-wide text-slate-500">Category</TableHead>
              <TableHead className="text-right text-xs font-semibold uppercase tracking-wide text-slate-500">Price</TableHead>
              <TableHead className="text-right text-xs font-semibold uppercase tracking-wide text-slate-500">Stock</TableHead>
              <TableHead className="text-right text-xs font-semibold uppercase tracking-wide text-slate-500">Actions</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {isLoadingProducts ? (
              <TableRow>
                <TableCell colSpan={6} className="py-12 text-center">
                  <Loader2 className="mx-auto h-8 w-8 animate-spin text-slate-400" />
                </TableCell>
              </TableRow>
            ) : filteredProducts.length === 0 ? (
              <TableRow>
                <TableCell colSpan={6} className="py-12 text-center text-slate-500">
                  No products found
                </TableCell>
              </TableRow>
            ) : (
              filteredProducts.map((product) => (
                <TableRow key={product.id} className="border-slate-200 hover:bg-slate-50/70">
                  <TableCell>
                    <img
                      src={product.imageUrl}
                      alt={product.name}
                      className="h-14 w-14 rounded-xl border border-slate-200 object-cover"
                    />
                  </TableCell>
                  <TableCell>
                    <div>
                      <p className="font-semibold text-slate-800">{product.name}</p>
                      <p className="max-w-[220px] truncate text-sm text-slate-500">{product.description}</p>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant="secondary" className="rounded-md bg-slate-100 text-slate-700 hover:bg-slate-100">
                      {categories.find((c) => c.id === product.categoryId)?.name || 'Unknown'}
                    </Badge>
                  </TableCell>
                  <TableCell className="text-right font-semibold text-slate-800">${product.price.toFixed(2)}</TableCell>
                  <TableCell className="text-right">
                    <Badge
                      className={
                        product.stockQuantity > 20
                          ? 'rounded-md bg-emerald-100 text-emerald-700 hover:bg-emerald-100'
                          : product.stockQuantity > 0
                            ? 'rounded-md bg-amber-100 text-amber-700 hover:bg-amber-100'
                            : 'rounded-md bg-rose-100 text-rose-700 hover:bg-rose-100'
                      }
                    >
                      {product.stockQuantity} in stock
                    </Badge>
                  </TableCell>
                  <TableCell className="text-right">
                    <div className="flex justify-end gap-2">
                      <Button
                        variant="outline"
                        size="icon"
                        className="h-8 w-8 rounded-lg border-slate-200 bg-white"
                        onClick={() => handleOpenEditDialog(product)}
                      >
                        <Pencil className="h-4 w-4 text-slate-600" />
                      </Button>
                      <Button
                        variant="outline"
                        size="icon"
                        className="h-8 w-8 rounded-lg border-rose-200 bg-rose-50"
                        onClick={() => {
                          setProductToDelete(product);
                          setIsDeleteDialogOpen(true);
                        }}
                      >
                        <Trash2 className="h-4 w-4 text-rose-600" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </div>

      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent className="max-w-lg rounded-2xl border-slate-200 bg-white">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2 text-slate-900">
              <Package className="h-5 w-5 text-slate-600" />
              {selectedProduct ? 'Edit Product' : 'Add New Product'}
            </DialogTitle>
            <DialogDescription>
              {selectedProduct ? 'Update product details' : 'Add a new product to your catalog'}
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="name">Product Name *</Label>
              <Input
                id="name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                placeholder="Enter product name"
                className="rounded-xl border-slate-200"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="Enter product description"
                rows={3}
                className="rounded-xl border-slate-200"
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="price">Price *</Label>
                <Input
                  id="price"
                  type="number"
                  step="0.01"
                  value={formData.price}
                  onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                  placeholder="0.00"
                  className="rounded-xl border-slate-200"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="stock">Stock</Label>
                <Input
                  id="stock"
                  type="number"
                  value={formData.stockQuantity}
                  onChange={(e) => setFormData({ ...formData, stockQuantity: e.target.value })}
                  placeholder="0"
                  className="rounded-xl border-slate-200"
                />
              </div>
            </div>
            <div className="space-y-2">
              <Label htmlFor="category">Category *</Label>
              <Select
                value={formData.categoryId.toString()}
                onValueChange={(value) => setFormData({ ...formData, categoryId: value })}
              >
                <SelectTrigger className="rounded-xl border-slate-200">
                  <SelectValue placeholder="Select category" />
                </SelectTrigger>
                <SelectContent>
                  {categories.map((category) => (
                    <SelectItem key={category.id} value={category.id.toString()}>
                      {category.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="imageUrl">Product Image</Label>
              <div className="flex gap-2">
                <Input
                  id="imageUrl"
                  value={formData.imageUrl}
                  onChange={(e) => setFormData({ ...formData, imageUrl: e.target.value })}
                  placeholder="Image URL"
                  className="rounded-xl border-slate-200"
                />
                <div className="relative">
                  <Input
                    type="file"
                    id="fileUpload"
                    className="hidden"
                    accept="image/*"
                    onChange={handleFileUpload}
                    disabled={isUploading}
                  />
                  <Button
                    type="button"
                    variant="outline"
                    className="h-10 w-10 rounded-xl border-slate-200"
                    onClick={() => document.getElementById('fileUpload')?.click()}
                    disabled={isUploading}
                  >
                    {isUploading ? <Loader2 className="h-4 w-4 animate-spin" /> : <Plus className="h-4 w-4" />}
                  </Button>
                </div>
              </div>
            </div>
          </div>

          <DialogFooter>
            <Button variant="outline" className="rounded-xl border-slate-200" onClick={() => setIsDialogOpen(false)}>
              Cancel
            </Button>
            <Button className="rounded-xl" onClick={handleSaveProduct}>
              {selectedProduct ? 'Save Changes' : 'Add Product'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <AlertDialog open={isDeleteDialogOpen} onOpenChange={setIsDeleteDialogOpen}>
        <AlertDialogContent className="rounded-2xl border-slate-200 bg-white">
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Product</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete "{productToDelete?.name}"? This action cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel className="rounded-xl border-slate-200">Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={handleDeleteProduct}
              className="rounded-xl bg-rose-600 text-white hover:bg-rose-700"
            >
              <Boxes className="mr-1 h-4 w-4" />
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
};

export default Products;

import { createSlice, createAsyncThunk, PayloadAction } from '@reduxjs/toolkit';
import { Order } from '@/types';
import * as adminService from '@/services/adminService';
import * as orderService from '@/services/orderService';

interface OrderState {
    items: Order[];
    currentOrder: orderService.PlaceOrderResponse | null;
    status: 'idle' | 'loading' | 'succeeded' | 'failed';
    error: string | null;
}

const initialState: OrderState = {
    items: [],
    currentOrder: null,
    status: 'idle',
    error: null,
};

export const fetchAdminOrders = createAsyncThunk('orders/fetchAdminOrders', async () => {
    return await adminService.getAdminOrders();
});

export const updateOrderStatus = createAsyncThunk('orders/updateStatus', async ({ orderId, status }: { orderId: number; status: string }) => {
    await adminService.updateOrderStatus(orderId, status);
    return { orderId, status };
});

export const placeOrder = createAsyncThunk('orders/placeOrder', async (orderData: orderService.PlaceOrderRequest) => {
    return await orderService.placeOrder(orderData);
});

export const uploadPaymentReceipt = createAsyncThunk(
    'orders/uploadPaymentReceipt',
    async ({ orderId, file }: { orderId: number; file: File }) => {
        return await orderService.uploadPaymentReceipt(orderId, file);
    }
);

const orderSlice = createSlice({
    name: 'orders',
    initialState,
    reducers: {
        resetCurrentOrder(state) {
            state.currentOrder = null;
        }
    },
    extraReducers: (builder) => {
        builder
            // Fetch Orders
            .addCase(fetchAdminOrders.pending, (state) => {
                state.status = 'loading';
            })
            .addCase(fetchAdminOrders.fulfilled, (state, action: PayloadAction<Order[]>) => {
                state.status = 'succeeded';
                state.items = action.payload;
            })
            .addCase(fetchAdminOrders.rejected, (state, action) => {
                state.status = 'failed';
                state.error = action.error.message || 'Failed to fetch orders';
            })
            // Update Status
            .addCase(updateOrderStatus.fulfilled, (state, action) => {
                const { orderId, status } = action.payload;
                const order = state.items.find(o => o.id === orderId);
                if (order) {
                    order.status = status as any;
                }
            })
            // Place Order
            .addCase(placeOrder.pending, (state) => {
                state.status = 'loading';
                state.error = null;
            })
            .addCase(placeOrder.fulfilled, (state, action) => {
                state.status = 'succeeded';
                state.currentOrder = action.payload;
            })
            .addCase(placeOrder.rejected, (state, action) => {
                state.status = 'failed';
                state.error = action.error.message || 'Failed to place order';
            });
    },
});

export const { resetCurrentOrder } = orderSlice.actions;
export default orderSlice.reducer;

